sap.ui.define(['exports', 'sap/ui/model/json/JSONModel', 'sap/ui/core/Fragment'], (function (exports, JSONModel, Fragment) { 'use strict';

  const popoverModules = [
    {
      icon: "sap-icon://text-formatting",
      name: "Schriftgröße",
      expandable: true,

    },
    {
      icon: "sap-icon://contrast",
      name: "Kontrastmodus",
      expandable: true,

    },
    {
      icon: "sap-icon://brightness",
      name: "Blaufilter  ",
      expandable: true,

    },
    {
      icon: "sap-icon://night-view",
      name: "Nachtmodus",
      expandable: true,

    },
    {
      icon: "sap-icon://microphone",
      name: "Webseite vorlesen",
      expandable: true,

    },
    {
      icon: "sap-icon://keyboard-and-mouse",
      name: "Tab Navigation",
      expandable: true,

    },
    {
      icon: "sap-icon://palette",
      name: "Farbschwäche",
      expandable: true,

    },
    {
      icon: "sap-icon://picture",
      name: "Bilder ausblenden",
      expandable: true,

    }
  ];

  // Bu bayrak, stilin daha önce yüklenip yüklenmediğini kontrol etmek için
  // bu modülün içinde saklanır. Dışarıdan erişilemez.
  let isCustomStyleLoaded = false;

  /**
   * Uygulamanın <head> bölümüne özel stil dosyasını yalnızca bir kez ekler.
   * Bu fonksiyon, paketin 'package.json' dosyasındaki adını bilir.
   */
  function loadCustomStyleOnce() {
    // Eğer daha önce yüklendiyse, hiçbir şey yapmadan çık.
    if (isCustomStyleLoaded) {
      return;
    }

    try {
      // 1. ADIM: Yolu dinamik ve güvenilir bir şekilde al.
      // DİKKAT: "new-npm-package" kısmını kendi paket adınızla değiştirmelisiniz.
      // Örnekte "ui5_bfsg_popover" kullanmıştınız, o şekilde de olabilir.
      const sCorrectPath = sap.ui.require.toUrl("new-npm-package/css/style.css");

      // 2. ADIM: Link etiketini oluştur ve ekle
      const link = document.createElement("link");
      link.id = "new-npm-package-styles"; // Tekrar kontrol için benzersiz bir ID ata.
      link.rel = "stylesheet";
      link.type = "text/css";
      link.href = sCorrectPath; // Doğru ve dinamik yolu kullan.
      document.head.appendChild(link);
      
      // 3. ADIM: Bayrağı güncelle ki bir daha yüklenmesin.
      isCustomStyleLoaded = true;
      console.log("Custom CSS loaded successfully from:", sCorrectPath);
    } catch (error) {
        console.error("Failed to load custom CSS:", error);
    }
  }

  function updateFontSize(oModel, oView, action) {
      console.log("Font size action:", action);
      const currentSize = oModel.getProperty("/font/fontSize");
      let newSize = currentSize;

      if (action === "increase" && currentSize < 40) {
          newSize += 2;
      } else if (action === "decrease" && currentSize > 10) {
          newSize -= 2;
      } else if (action === "reset") {
          newSize = 16;
      }

      oModel.setProperty("/font/fontSize", newSize);
      applyFontSizeToView(oView, newSize);
  }

  function applyFontSizeToView(oView, fontSize) {
      oView.findAggregatedObjects(true, (oControl) => {
          const oDomRef = oControl.getDomRef();
          if (oDomRef) {
              oDomRef.style.fontSize = fontSize + "px";
              updateChildFontSize(oDomRef, fontSize);
          }
          return false;
      });
  }

  function updateChildFontSize(domElement, fontSize) {
      const childNodes = domElement.querySelectorAll("*");
      childNodes.forEach((child) => {
          child.style.fontSize = fontSize + "px";
      });
  }

  var fontSizeUtils = {
      updateFontSize,
      applyFontSizeToView,
      updateChildFontSize,
  };

  const fontModel = new JSONModel({
      font: {
          fontSize: 16
      }
  });
  this.getView().setModel(fontModel, "font");

  const openPopover = async (controller, oEvent) => {
    const oView = controller.getView();
    const sFragmentId = oView.getId();
    if (!controller._pPopover) {
      loadCustomStyleOnce();

      controller._pPopover = Fragment.load({
        id: sFragmentId,
        name: "new-npm-package.Popover",
      }).then((oPopover) => {
        oView.addDependent(oPopover);
        return oPopover;
      });
    }

    const oPopover = await controller._pPopover;

    const aModulesData = popoverModules;
    const oModulesModel = new JSONModel({ items: aModulesData });
    oPopover.setModel(oModulesModel, "modules");

    const increaseBtn = Fragment.byId(sFragmentId, "increaseFontSizeButton");
  const decreaseBtn = Fragment.byId(sFragmentId, "decreaseFontSizeButton");

  if (increaseBtn && !increaseBtn.hasListeners("press")) {
      increaseBtn.attachPress(() => {
          const fontModel = oView.getModel("font");
          fontSizeUtils.updateFontSize(fontModel, oView, "increase");
      });
  }

  if (decreaseBtn && !decreaseBtn.hasListeners("press")) {
      decreaseBtn.attachPress(() => {
          const fontModel = oView.getModel("font");
          fontSizeUtils.updateFontSize(fontModel, oView, "decrease");
      });
  }

    const closeButton = Fragment.byId(sFragmentId, "closePopoverButton");

    if (closeButton && !closeButton.hasListeners("press")) {
      closeButton.attachPress(() => oPopover.close());
    }

    oPopover.openBy(oEvent.getSource());
  };

  const __esModule = true ;

  exports.__esModule = __esModule;
  exports.openPopover = openPopover;

}));
