sap.ui.define(['exports', 'sap/ui/model/json/JSONModel', 'sap/ui/core/Fragment'], (function (exports, JSONModel, Fragment) { 'use strict';

  const popoverModules = [
    {
      id: "fontSize",
      icon: "sap-icon://text-formatting",
      name: "Schriftgröße",
      expandable: true,
      expanded: false,
    },

    {
      id: "contrast",
      icon: "sap-icon://microphone",
      name: "Webseite vorlesen",
      expandable: true,
      expanded: false,
    },
    {
      id: "colorBlindness",
      icon: "sap-icon://palette",
      name: "Farbschwäche",
      expandable: true,
      expanded: false,
    },
    {
      id: "blauFilter",
      icon: "sap-icon://light-mode",
      name: "Blaufilter aktivieren",
      expandable: true,
      expanded: false,
    },
    {
      id: "darkMode",
      icon: "sap-icon://paint-bucket",
      name: "Kontrastmodus",
      expandable: false,
      expanded: false,
    },

    {
      id: "nightMode",
      icon: "sap-icon://dark-mode",
      name: "Nachtmodus",
      expandable: false,
      expanded: false,
    },

    {
      id: "tabNavigation",
      icon: "sap-icon://keyboard-and-mouse",
      name: "Tab Navigation",
      expandable: false,
      expanded: false,
    },

    {
      id: "toggleImages",
      icon: "sap-icon://picture",
      name: "Bilder ausblenden",
      expandable: false,
      expanded: false,
    },
  ];

  let isCustomStyleLoaded = false;
  function loadCustomStyleOnce() {
    if (isCustomStyleLoaded) {
      return;
    }

    try {
      const sCorrectPath = sap.ui.require.toUrl("abics_accessibility_popover/css/style.css");

      const link = document.createElement("link");
      link.id = "abics_accessibility_popover-styles";
      link.rel = "stylesheet";
      link.type = "text/css";
      link.href = sCorrectPath;
      document.head.appendChild(link);
      
      isCustomStyleLoaded = true;
    } catch (error) {
        console.error("Failed to load custom CSS:", error);
    }
  }

  let _defaultFontSize = null;

  let _settingsModel = null;


  const initFontSizer = (oSettingsModel) => {
      if (!_defaultFontSize) {
          _defaultFontSize = window.getComputedStyle(document.documentElement).getPropertyValue("font-size");
      }
      _settingsModel = oSettingsModel;
  };


  const _updateView = () => {
      if (_settingsModel && _defaultFontSize) {
          const step = _settingsModel.getProperty("/fontStep");
          const newSize = `calc(${_defaultFontSize} + ${step * 2}px)`;
          document.documentElement.style.fontSize = newSize;
      }
  };


  const onIncreaseFontSize = () => {
      if (!_settingsModel) return;
      let currentStep = _settingsModel.getProperty("/fontStep");
      if (currentStep < 5) {
          _settingsModel.setProperty("/fontStep", currentStep + 1);
          _updateView();
      }
  };


  const onDecreaseFontSize = () => {
      if (!_settingsModel) return;
      let currentStep = _settingsModel.getProperty("/fontStep");
          if (currentStep > -3) {
          _settingsModel.setProperty("/fontStep", currentStep - 1);
          _updateView();
      }
  };

  const onResetFontSize = () => {
      if (!_settingsModel) return;
      _settingsModel.setProperty("/fontStep", 0);
      document.documentElement.style.fontSize = _defaultFontSize;
  };

  let synth = window.speechSynthesis;
  let utterance = null;
  let isReading = false;
  let settingsModel = null;

  const initTextToSpeech = (oSettingsModel) => {
      settingsModel = oSettingsModel;
  };

  const startReading = () => {
      if (isReading) return;
      stopReading();
      const text = document.body.innerText;
      utterance = new SpeechSynthesisUtterance(text);
      utterance.rate = settingsModel?.getProperty("/ttsRate") || 1;
      utterance.volume = settingsModel?.getProperty("/ttsVolume") || 1;
      synth.speak(utterance);
      isReading = true;
      utterance.onend = () => { isReading = false; };
  };

  const stopReading = () => {
      if (synth.speaking) synth.cancel();
      isReading = false;
  };

  const setTTSRate = (rate) => {
      settingsModel?.setProperty("/ttsRate", rate);
  };

  const setTTSVolume = (volume) => {
      settingsModel?.setProperty("/ttsVolume", volume);
  };

  let hoverActive = false;
  function hoverHandler(e) {
      if (e.target && e.target.innerText) {
          stopReading();
          utterance = new SpeechSynthesisUtterance(e.target.innerText);
          utterance.rate = settingsModel?.getProperty("/ttsRate") || 1;
          utterance.volume = settingsModel?.getProperty("/ttsVolume") || 1;
          synth.speak(utterance);
      }
  }

  const enableHoverRead = () => {
      if (!hoverActive) {
          document.body.addEventListener("mouseover", hoverHandler);
          hoverActive = true;
      }
  };

  const disableHoverRead = () => {
      if (hoverActive) {
          document.body.removeEventListener("mouseover", hoverHandler);
          hoverActive = false;
      }
  };

  const FILTERS = {
      none: '',
      protanopia: 'url(#protanopia)',
      deuteranopia: 'url(#deuteranopia)',
      tritanopia: 'url(#tritanopia)',
      achromatopsia: 'grayscale(1)'
  };


  function injectSVGFilters() {
      if (document.getElementById('color-blindness-filters')) return;
      const svgNS = 'http://www.w3.org/2000/svg';
      const svg = document.createElementNS(svgNS, 'svg');
      svg.setAttribute('id', 'color-blindness-filters');
      svg.setAttribute('style', 'position:absolute;width:0;height:0;');
      svg.innerHTML = `
      <filter id="protanopia">
        <feColorMatrix type="matrix" values="0.567 0.433 0 0 0 0.558 0.442 0 0 0 0 0.242 0.758 0 0 0 0 0 1 0"/>
      </filter>
      <filter id="deuteranopia">
        <feColorMatrix type="matrix" values="0.625 0.375 0 0 0 0.7 0.3 0 0 0 0 0.3 0.7 0 0 0 0 0 1 0"/>
      </filter>
      <filter id="tritanopia">
        <feColorMatrix type="matrix" values="0.95 0.05 0 0 0 0 0.433 0.567 0 0 0 0.475 0.525 0 0 0 0 0 1 0"/>
      </filter>
    `;
      document.body.appendChild(svg);
  }

  const applyColorBlindness = (type) => {
      injectSVGFilters();
      const filter = FILTERS[type] || '';
      document.documentElement.style.filter = filter;
  };

  const resetColorBlindness = () => {
      document.documentElement.style.filter = '';
  };

  function getFilter(level) {
      const sepia = (level / 100) * 0.6;
      const hue = -20 * (level / 100);
      const brightness = 1 - (level / 100) * 0.1;
      return `brightness(${brightness}) sepia(${sepia}) hue-rotate(${hue}deg)`;
  }

  const enableBlueLightFilter = (level = 50) => {
      document.documentElement.style.filter = getFilter(level);
  };

  const disableBlueLightFilter = () => {
      document.documentElement.style.filter = '';
  };

  let isDarkMode = false;

  const toggleNightMode = () => {
      isDarkMode = !isDarkMode;
      if (isDarkMode) {
          sap.ui.getCore().applyTheme("sap_horizon_dark");
      } else {
          sap.ui.getCore().applyTheme("sap_horizon");
      }
      return isDarkMode;
  };

  const disableNightMode = () => {
      if (isDarkMode) {
          sap.ui.getCore().applyTheme("sap_horizon");
          isDarkMode = false;
      }
  };

  const STORAGE_KEY = "eye-able-images-hidden";

  let imagesHidden = false;
  let observer = null;

  function setVisibilityAll(selector, value) {
      document.querySelectorAll(selector).forEach(el => el.style.visibility = value);
  }

  function setVisibilityBackgroundImage(value) {
      document.querySelectorAll('[style*="background-image"]').forEach(el => el.style.visibility = value);
  }

  function setVisibilityInShadowRoots(value) {
      document.querySelectorAll('*').forEach(el => {
          if (el.shadowRoot) {
              el.shadowRoot.querySelectorAll('img,svg,.sapUiIcon,.sapMImg,.sapMBtnIcon,.sapFAvatar,.sapMIllustratedMessage-illu').forEach(shEl => shEl.style.visibility = value);
              el.shadowRoot.querySelectorAll('[style*="background-image"]').forEach(shEl => shEl.style.visibility = value);
          }
      });
  }

  const toggleImages = () => {
      imagesHidden = !imagesHidden;
      if (imagesHidden) {
          hideImages();
          startImageObserver();
      } else {
          showImages();
          stopImageObserver();
      }
      localStorage.setItem(STORAGE_KEY, imagesHidden);
      console.log(`Images are now ${imagesHidden ? 'hidden' : 'visible'}.`);
      return imagesHidden;
  };

  const hideImages = () => {
      setVisibilityAll('img', 'hidden');
      setVisibilityAll('svg', 'hidden');
      setVisibilityAll('.sapMImg', 'hidden');
      setVisibilityAll('.sapMBtnIcon', 'hidden');
      setVisibilityAll('.sapUiIcon', 'hidden');
      setVisibilityAll('.sapFAvatar', 'hidden');
      setVisibilityAll('.sapMIllustratedMessage-illu', 'hidden');
      setVisibilityBackgroundImage('hidden');
      setVisibilityInShadowRoots('hidden');
  };

  const showImages = () => {
      setVisibilityAll('img', '');
      setVisibilityAll('svg', '');
      setVisibilityAll('.sapMImg', '');
      setVisibilityAll('.sapMBtnIcon', '');
      setVisibilityAll('.sapUiIcon', '');
      setVisibilityAll('.sapFAvatar', '');
      setVisibilityAll('.sapMIllustratedMessage-illu', '');
      setVisibilityBackgroundImage('');
      setVisibilityInShadowRoots('');
  };

  const startImageObserver = () => {
      if (observer) return;
      observer = new MutationObserver(() => {
          if (imagesHidden) hideImages();
      });
      observer.observe(document.body, { childList: true, subtree: true });
  };

  const stopImageObserver = () => {
      if (observer) observer.disconnect();
      observer = null;
  };

  let contrastActive = false;

  const toggleContrastMode = () => {
      contrastActive = !contrastActive;
      if (contrastActive) {
          document.body.style.filter = 'invert(1) grayscale(1)';
          document.body.style.backgroundColor = '#000';
          document.body.style.color = '#fff';
      } else {
          document.body.style.filter = '';
          document.body.style.backgroundColor = '';
          document.body.style.color = '';
      }
      return contrastActive;
  };

  const isContrastModeActive = () => contrastActive;

  const resetAll = () => {
      onResetFontSize();
      stopReading();
      setTTSRate(1);
      setTTSVolume(1);
      disableHoverRead();
      resetColorBlindness();
      disableBlueLightFilter();
      disableNightMode();
      showImages();
      if (isContrastModeActive()) toggleContrastMode();
  };

  const oSettingsModel = new JSONModel({
      fontStep: 0,
      ttsRate: 1,
      ttsVolume: 1,
      ttsHover: false,
      colorBlindnessType: 'none',
      blueLightFilterLevel: 50,
      blueLightFilterActive: false,
      fontSizeExpanded: false,
      ttsExpanded: false,
      colorBlindnessExpanded: false,
      blaufilterExpanded: false,
      nightModeActive: false,
      toggleImagesActive: false,
      contrastModeActive: false,
      contrastModeExpanded: false,
      contrastBgColor: "#000000",
      contrastTextColor: "#FFFFFF",
      contrastRatio: "21:1",
      contrastReadable: "LESBAR?",
      contrastUnderlineLinks: true
  });

  const popoverInternalController = {
      onFontSizeToolbarPress: function () {
          const expanded = oSettingsModel.getProperty("/fontSizeExpanded");
          oSettingsModel.setProperty("/fontSizeExpanded", !expanded);
      },
      onTTSToolbarPress: function () {
          const expanded = oSettingsModel.getProperty("/ttsExpanded");
          oSettingsModel.setProperty("/ttsExpanded", !expanded);
      },
      onColorBlindnessToolbarPress: function () {
          const expanded = oSettingsModel.getProperty("/colorBlindnessExpanded");
          oSettingsModel.setProperty("/colorBlindnessExpanded", !expanded);
      },
      onBlaufilterToolbarPress: function () {
          const expanded = oSettingsModel.getProperty("/blaufilterExpanded");
          oSettingsModel.setProperty("/blaufilterExpanded", !expanded);
          if (!expanded) {
              enableBlueLightFilter(oSettingsModel.getProperty("/blueLightFilterLevel"));
              oSettingsModel.setProperty("/blueLightFilterActive", true);
          } else {
              disableBlueLightFilter();
              oSettingsModel.setProperty("/blueLightFilterActive", false);
          }
      },
      onNightModeToolbarPress: function () {
          const active = toggleNightMode();
          oSettingsModel.setProperty("/nightModeActive", active);
      },
      onToggleImagesToolbarPress: function () {
          const active = toggleImages();
          oSettingsModel.setProperty("/toggleImagesActive", active);
      },
      onContrastModeToolbarPress: function () {
          const active = toggleContrastMode();
          oSettingsModel.setProperty("/contrastModeActive", active);
      },
      onResetAllToolbarPress: function () {
          resetAll();
          oSettingsModel.setProperty("/fontStep", 0);
          oSettingsModel.setProperty("/ttsRate", 1);
          oSettingsModel.setProperty("/ttsVolume", 1);
          oSettingsModel.setProperty("/ttsHover", false);
          oSettingsModel.setProperty("/colorBlindnessType", 'none');
          oSettingsModel.setProperty("/blueLightFilterLevel", 50);
          oSettingsModel.setProperty("/blueLightFilterActive", false);
          oSettingsModel.setProperty("/fontSizeExpanded", false);
          oSettingsModel.setProperty("/ttsExpanded", false);
          oSettingsModel.setProperty("/colorBlindnessExpanded", false);
          oSettingsModel.setProperty("/blaufilterExpanded", false);
          oSettingsModel.setProperty("/nightModeActive", false);
          oSettingsModel.setProperty("/toggleImagesActive", false);
          oSettingsModel.setProperty("/contrastModeActive", false);
      },
      onContrastPresetPress: function (oEvent) {
          const key = oEvent.getSource().getCustomData()[0].getValue();
          let bg = "#000000", text = "#FFFFFF";
          if (key === "black-white") { bg = "#000000"; text = "#FFFFFF"; }
          if (key === "yellow-black") { bg = "#FFFF00"; text = "#000000"; }
          if (key === "red-black") { bg = "#FF0000"; text = "#000000"; }
          if (key === "green-black") { bg = "#00FF00"; text = "#000000"; }
          oSettingsModel.setProperty("/contrastBgColor", bg);
          oSettingsModel.setProperty("/contrastTextColor", text);
          updateContrastPreview();
      },
      onContrastApply: function () {
          applyContrastToDOM(
              oSettingsModel.getProperty("/contrastBgColor"),
              oSettingsModel.getProperty("/contrastTextColor"),
              oSettingsModel.getProperty("/contrastUnderlineLinks")
          );
          oSettingsModel.setProperty("/contrastModeActive", true);
      },
      onContrastReset: function () {
          removeContrastFromDOM();
          oSettingsModel.setProperty("/contrastBgColor", "#000000");
          oSettingsModel.setProperty("/contrastTextColor", "#FFFFFF");
          oSettingsModel.setProperty("/contrastUnderlineLinks", true);
          oSettingsModel.setProperty("/contrastModeActive", false);
          updateContrastPreview();
      },
      onUnderlineLinksToggle: function (oEvent) {
          oSettingsModel.setProperty("/contrastUnderlineLinks", oEvent.getParameter("selected"));
          updateContrastPreview();
      },
      onPopoverClosePress: function (oEvent) {
          let oPopover = oEvent.getSource().getParent();
          while (oPopover && !(oPopover.isA && oPopover.isA("sap.m.Popover"))) {
              oPopover = oPopover.getParent && oPopover.getParent();
          }
          if (oPopover && oPopover.close) {
              oPopover.close();
          }
      }
  };

  const openAbicsAccessibilityPopover = async (controller, oEvent) => {
      // DEBUG: Parameter checks
      if (!controller || typeof controller.getView !== "function") {
          console.error("[abics_accessibility_popover] ERROR: The controller parameter must be a UI5 Controller!", controller);
          throw new Error("The controller parameter must be a UI5 Controller!");
      }
      if (!oEvent || typeof oEvent.getSource !== "function") {
          console.error("[abics_accessibility_popover] ERROR: The oEvent parameter must be a UI5 Event!", oEvent);
          throw new Error("The oEvent parameter must be a UI5 Event!");
      }
      const oView = controller.getView();
      const sFragmentId = oView.getId();

      if (!controller._pPopover) {
          console.debug("[abics_accessibility_popover] Popover is being loaded for the first time.");
          loadCustomStyleOnce();
          initFontSizer(oSettingsModel);
          initTextToSpeech(oSettingsModel);

          controller._pPopover = Fragment.load({
              id: sFragmentId,
              name: "abics_accessibility_popover.Popover",
              controller: popoverInternalController
          }).then((oPopover) => {
              if (!oPopover) {
                  console.error("[abics_accessibility_popover] ERROR: Fragment could not be loaded!");
                  throw new Error("Popover Fragment could not be loaded!");
              }
              oView.addDependent(oPopover);

              try {
                  oPopover.setModel(oSettingsModel, "settings");
              } catch (err) {
                  console.error("[abics_accessibility_popover] ERROR: Model could not be assigned!", err);
              }

              const closeButton = Fragment.byId(sFragmentId, "closePopoverButton");
              const increaseButton = Fragment.byId(sFragmentId, "increaseFontButton");
              const decreaseButton = Fragment.byId(sFragmentId, "decreaseFontButton");
              const resetButton = Fragment.byId(sFragmentId, "resetFontButton");

              if (!closeButton) console.warn("[abics_accessibility_popover] WARNING: closePopoverButton not found!");
              if (!increaseButton) console.warn("[abics_accessibility_popover] WARNING: increaseFontButton not found!");
              if (!decreaseButton) console.warn("[abics_accessibility_popover] WARNING: decreaseFontButton not found!");
              if (!resetButton) console.warn("[abics_accessibility_popover] WARNING: resetFontButton not found!");

              closeButton?.attachPress(() => oPopover.close());
              increaseButton?.attachPress(onIncreaseFontSize);
              decreaseButton?.attachPress(onDecreaseFontSize);
              resetButton?.attachPress(onResetFontSize);

              const ttsStartButton = Fragment.byId(sFragmentId, "ttsStartButton");
              const ttsStopButton = Fragment.byId(sFragmentId, "ttsStopButton");
              const ttsRateSlider = Fragment.byId(sFragmentId, "ttsRateSlider");
              const ttsVolumeSlider = Fragment.byId(sFragmentId, "ttsVolumeSlider");
              const ttsHoverSwitch = Fragment.byId(sFragmentId, "ttsHoverSwitch");

              ttsStartButton?.attachPress(startReading);
              ttsStopButton?.attachPress(stopReading);
              ttsRateSlider?.attachChange((e) => setTTSRate(e.getParameter("value")));
              ttsVolumeSlider?.attachChange((e) => setTTSVolume(e.getParameter("value")));
              ttsHoverSwitch?.attachChange((e) => {
                  const active = e.getParameter("state");
                  oSettingsModel.setProperty("/ttsHover", active);
                  if (active) enableHoverRead(); else disableHoverRead();
              });

              const colorBlindnessSelect = Fragment.byId(sFragmentId, "colorBlindnessSelect");
              const colorBlindnessResetButton = Fragment.byId(sFragmentId, "colorBlindnessResetButton");
              colorBlindnessSelect?.attachChange((e) => {
                  const type = e.getParameter("selectedItem").getKey();
                  oSettingsModel.setProperty("/colorBlindnessType", type);
                  applyColorBlindness(type);
              });
              colorBlindnessResetButton?.attachPress(() => {
                  oSettingsModel.setProperty("/colorBlindnessType", "none");
                  resetColorBlindness();
              });

              const blueLightFilterSlider = Fragment.byId(sFragmentId, "blueLightFilterSlider");
              const blueLightFilterResetButton = Fragment.byId(sFragmentId, "blueLightFilterResetButton");
              blueLightFilterSlider?.attachChange((e) => {
                  const level = e.getParameter("value");
                  oSettingsModel.setProperty("/blueLightFilterLevel", level);
                  enableBlueLightFilter(level);
              });
              blueLightFilterResetButton?.attachPress(() => {
                  oSettingsModel.setProperty("/blueLightFilterLevel", 0);
                  disableBlueLightFilter();
              });

              const blueLightFilterHeaderButton = Fragment.byId(sFragmentId, "blueLightFilterHeaderButton");
              blueLightFilterHeaderButton?.attachPress(() => {
                  const active = oSettingsModel.getProperty("/blueLightFilterActive");
                  if (!active) {
                      enableBlueLightFilter(oSettingsModel.getProperty("/blueLightFilterLevel"));
                  } else {
                      disableBlueLightFilter();
                  }
                  oSettingsModel.setProperty("/blueLightFilterActive", !active);
              });

              oPopover.onPanelHeaderPress = function (oEvent) {
                  const context = oEvent.getSource().getBindingContext("modules");
                  if (context) {
                      const name = context.getProperty("name");
                      const expanded = context.getProperty("expanded");
                      context.getModel().setProperty(context.getPath() + "/expanded", !expanded);
                      if (name === "Blaufilter aktivieren") {
                          if (!expanded) {
                              const level = oSettingsModel.getProperty("/blueLightFilterLevel");
                              enableBlueLightFilter(level);
                          } else {
                              disableBlueLightFilter();
                          }
                      }
                  }
              };

              return oPopover;
          }).catch((err) => {
              console.error("[abics_accessibility_popover] ERROR: Error occurred during Fragment.load!", err);
              throw err;
          });
      }

      const oPopover = await controller._pPopover;

      try {
          oPopover.setModel(new JSONModel({ items: popoverModules }), "modules");
      } catch (err) {
          console.error("[abics_accessibility_popover] ERROR: Modules model could not be assigned!", err);
      }
      try {
          oPopover.openBy(oEvent.getSource());
      } catch (err) {
          console.error("[abics_accessibility_popover] ERROR: Error during openBy!", err);
      }
  };

  function updateContrastPreview() {
      const bg = oSettingsModel.getProperty("/contrastBgColor");
      const text = oSettingsModel.getProperty("/contrastTextColor");
      const ratio = getContrastRatio(bg, text);
      oSettingsModel.setProperty("/contrastRatio", ratio.ratioText);
      oSettingsModel.setProperty("/contrastReadable", ratio.readable ? "LESBAR" : "NICHT LESBAR");
  }
  function getContrastRatio(bg, text) {
      function luminance(hex) {
          let c = hex.replace('#', '');
