sap.ui.define(['exports', 'sap/ui/model/json/JSONModel', 'sap/ui/core/Fragment'], (function (exports, JSONModel, Fragment) { 'use strict';

  const popoverModules = [
      {
        icon: "sap-icon://add",
        name: "module1",
      },
      {
        icon: "sap-icon://add",
        name: "module2",
      },
      {
        icon: "sap-icon://add",
        name: "module3",
      },
      {
        icon: "sap-icon://add",
        name: "module4",
      },
    ];

  const openPopover = async (controller, oEvent) => {
      
      const oView = controller.getView();
      const sFragmentId = oView.getId();
      if (!controller._pPopover) {
          // Artık 'sFragmentId' burada da erişilebilir durumda.
          controller._pPopover = Fragment.load({
              id: sFragmentId,
              name: "new-npm-package.Popover",
              // controller: { ... } // Bu kısım şimdilik boş
          }).then((oPopover) => {
              oView.addDependent(oPopover);
              return oPopover;
          });
      }

      const oPopover = await controller._pPopover;
      
      // Veri modelini bağla
      const aModulesData = popoverModules;
      const oModulesModel = new JSONModel({ items: aModulesData });
      oPopover.setModel(oModulesModel, "modules");

      // 'sFragmentId' burada da sorunsuz bir şekilde erişilebilir.
      const closeButton = Fragment.byId(sFragmentId, "closePopoverButton");

      if (closeButton && !closeButton.hasListeners("press")) { 
          closeButton.attachPress(() => oPopover.close());
      }
      
      oPopover.openBy(oEvent.getSource());
  };

  const __esModule = true ;

  exports.__esModule = __esModule;
  exports.openPopover = openPopover;

}));
