sap.ui.define(['exports', 'sap/ui/model/json/JSONModel'], (function (exports, JSONModel) { 'use strict';

  const popoverModules = [
      {
        icon: "sap-icon://add",
        name: "module1",
      },
      {
        icon: "sap-icon://add",
        name: "module2",
      },
      {
        icon: "sap-icon://add",
        name: "module3",
      },
      {
        icon: "sap-icon://add",
        name: "module4",
      },
    ];

  const openPopover = async (controller) => {
      // 1. Fragment'ın ihtiyaç duyacağı olay yöneticisi (event handler) fonksiyonlarını tanımla
      const oFragmentController = {
          
          // Kapatma fonksiyonu
          onCloseDialog: () => {
              // controller.oDialog, ana controller üzerinde sakladığımız diyalog referansıdır.
              // Bu, 'closure' sayesinde çalışır. Fonksiyon, tanımlandığı yerdeki
              // 'controller' değişkenine erişebilir.
              controller.oDialog.close();
          },

          // Liste elemanına tıklama fonksiyonu
          onModulePress: (oEvent) => {
              const oSelectedItem = oEvent.getSource().getBindingContext("modules").getObject();
              
              // Buradaki mantık tamamen paketin içinde kalır.
              // Kullanıcı bu log'u veya MessageBox'ı görmez (eğer eklersek).
              console.log(`(From inside the package) Module selected: ${oSelectedItem.name}`);
              
              // İstersen burada bir callback fonksiyonu çağırabilirsin,
              // ama şimdilik bu kadar yeterli.
          }
      };

      // 2. Fragment'ı YÜKLERKEN, 'controller' olarak kendi olay yöneticimizi veriyoruz.
      controller.oDialog ??= await controller.loadFragment({
          name: "new-npm-package.Popover",
          controller: oFragmentController // <-- EN ÖNEMLİ KISIM BURASI
      });

      // 3. Veri modelini oluştur ve diyaloğa bağla (bu kısım aynı kalıyor)
      const aModulesData = popoverModules;
      const oModulesModel = new JSONModel({ items: aModulesData });
      controller.oDialog.setModel(oModulesModel, "modules");

      // 4. Diyaloğu aç
      controller.oDialog.open();
  };ss;

  const __esModule = true ;

  exports.__esModule = __esModule;
  exports.openPopover = openPopover;

}));
