sap.ui.define(['exports', 'sap/ui/model/json/JSONModel', 'sap/ui/core/Fragment'], (function (exports, JSONModel, Fragment) { 'use strict';

  const popoverModules = [
    {
      icon: "sap-icon://text-formatting",
      name: "Schriftgröße",
      expandable: true,

    },
    {
      icon: "sap-icon://contrast",
      name: "Kontrastmodus",
      expandable: true,

    },
    {
      icon: "sap-icon://brightness",
      name: "Blaufilter aktivieren",
      expandable: true,

    },
    {
      icon: "sap-icon://night-view",
      name: "Nachtmodus",
      expandable: true,

    },
    {
      icon: "sap-icon://microphone",
      name: "Webseite vorlesen",
      expandable: true,

    },
    {
      icon: "sap-icon://keyboard-and-mouse",
      name: "Tab Navigation",
      expandable: true,

    },
    {
      icon: "sap-icon://palette",
      name: "Farbschwäche",
      expandable: true,

    },
    {
      icon: "sap-icon://picture",
      name: "Bilder ausblenden",
      expandable: true,

    }
  ];

  // Bu bayrak, stilin daha önce yüklenip yüklenmediğini kontrol etmek için
  // bu modülün içinde saklanır. Dışarıdan erişilemez.
  let isCustomStyleLoaded = false;

  /**
   * Uygulamanın <head> bölümüne özel stil dosyasını yalnızca bir kez ekler.
   * Bu fonksiyon, paketin 'package.json' dosyasındaki adını bilir.
   */
  function loadCustomStyleOnce() {
    // Eğer daha önce yüklendiyse, hiçbir şey yapmadan çık.
    if (isCustomStyleLoaded) {
      return;
    }

    try {
      // 1. ADIM: Yolu dinamik ve güvenilir bir şekilde al.
      // DİKKAT: "new-npm-package" kısmını kendi paket adınızla değiştirmelisiniz.
      // Örnekte "ui5_bfsg_popover" kullanmıştınız, o şekilde de olabilir.
      const sCorrectPath = sap.ui.require.toUrl("new-npm-package/css/style.css");

      // 2. ADIM: Link etiketini oluştur ve ekle
      const link = document.createElement("link");
      link.id = "new-npm-package-styles"; // Tekrar kontrol için benzersiz bir ID ata.
      link.rel = "stylesheet";
      link.type = "text/css";
      link.href = sCorrectPath; // Doğru ve dinamik yolu kullan.
      document.head.appendChild(link);
      
      // 3. ADIM: Bayrağı güncelle ki bir daha yüklenmesin.
      isCustomStyleLoaded = true;
      console.log("Custom CSS loaded successfully from:", sCorrectPath);
    } catch (error) {
        console.error("Failed to load custom CSS:", error);
    }
  }

  // Bu modül, paketin kendi dahili durumunu yönetir.
  const fontState = {
      fontSize: 16, // Varsayılan boyut (px)
      fontFamily: "'Segoe UI', Arial, sans-serif", // Varsayılan font ailesi
      fontColor: null // Varsayılan renk (null = değiştirme)
  };

  /**
   * Mevcut font durumunu döndürür.
   * @returns {object} Kopyalanmış font durumu nesnesi.
   */
  function getFontState() {
      // Nesnenin kendisini değil, bir kopyasını döndürerek dışarıdan değiştirilmesini engelle
      return { ...fontState };
  }

  /**
   * Yazı tipi boyutunu günceller.
   * @param {number} newSize - Yeni yazı tipi boyutu.
   */
  function setFontSize(newSize) {
      fontState.fontSize = newSize;
  }

  // Gelecekte font ailesini veya rengini değiştirmek isterseniz
  // buraya benzer setFontFamily, setFontColor fonksiyonları ekleyebilirsiniz.

  // Gerekli durum yönetimi fonksiyonlarını kendi modelimizden import edelim

  const MAX_SIZE = 40;
  const MIN_SIZE = 10;

  /**
   * Tüm sayfaya mevcut font ayarlarını uygular.
   * Bu fonksiyon bu modülün içinde özeldir (private).
   */
  function _applyStylesToBody() {
      const currentState = getFontState();
      const fontSize = currentState.fontSize;
      
      console.log(`Applying font size: ${fontSize}px`);

      const allElements = document.querySelectorAll('body, body *');
      
      allElements.forEach(element => {
          // Stilleri doğrudan ve UI5'i ezecek şekilde uygula
          element.style.setProperty('font-size', `${fontSize}px`, 'important');
      });
  }

  /**
   * Yazı tipi boyutunu artırır.
   */
  function increaseFontSize() {
      let currentSize = getFontState().fontSize;
      if (currentSize < MAX_SIZE) {
          currentSize += 2;
          setFontSize(currentSize); // Önce durumu güncelle
          _applyStylesToBody();      // Sonra DOM'a uygula
      }
  }

  /**
   * Yazı tipi boyutunu azaltır.
   */
  function decreaseFontSize() {
      let currentSize = getFontState().fontSize;
      if (currentSize > MIN_SIZE) {
          currentSize -= 2;
          setFontSize(currentSize); // Önce durumu güncelle
          _applyStylesToBody();      // Sonra DOM'a uygula
      }
  }

  const openPopover = async (controller, oEvent) => {
    const oView = controller.getView();
    const sFragmentId = oView.getId();

    if (!controller._pPopover) {
      // Popover ilk kez açılıyorsa CSS'i yükle
      loadCustomStyleOnce();

      // Fragment'ı yükle ve tüm kurulumu .then() bloğunun içinde yap
      controller._pPopover = Fragment.load({
        id: sFragmentId,
        name: "new-npm-package.Popover",
      }).then((oPopover) => {
        // 1. Fragment'ı View'e bağımlı yap
        oView.addDependent(oPopover);

        // 2. Popover'ın kendi menü listesi için modeli ata
        const aModulesData = popoverModules;
        const oModulesModel = new JSONModel({ items: aModulesData });
        oPopover.setModel(oModulesModel, "modules");

        // 3. Butonları bul ve olayları ata (En Önemli Kısım)
        const increaseButton = Fragment.byId(sFragmentId, "increaseFontButton");
        const decreaseButton = Fragment.byId(sFragmentId, "decreaseFontButton");
        const closeButton = Fragment.byId(sFragmentId, "closePopoverButton");

        if (increaseButton && !increaseButton.hasListeners("press")) {
          increaseButton.attachPress(increaseFontSize);
        }
        if (decreaseButton && !decreaseButton.hasListeners("press")) {
          decreaseButton.attachPress(decreaseFontSize);
        }
        if (closeButton && !closeButton.hasListeners("press")) {
          closeButton.attachPress(() => oPopover.close());
        }

        // 4. Kurulumu tamamlanmış Popover'ı döndür
        return oPopover;
      });
    }

    // Hazır Popover'ı al
    const oPopover = await controller._pPopover;
    
    // Popover'ı aç
    oPopover.openBy(oEvent.getSource());
  };

  const __esModule = true ;

  exports.__esModule = __esModule;
  exports.openPopover = openPopover;

}));

